&lt;!DOCTYPE html&gt;  &lt;html lang=&quot;en&quot;&gt;  &lt;head&gt;      &lt;meta charset=&quot;UTF-8&quot;&gt;      &lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot;&gt;      &lt;title&gt;Weather App&lt;/title&gt;      &lt;!-- 引入Vue --&gt;      &lt;script src=&quot;https://cdn.jsdelivr.net/npm/vue@2&quot;&gt;&lt;/script&gt;      &lt;!-- 引入Axios --&gt;      &lt;script src=&quot;https://cdn.jsdelivr.net/npm/axios/dist/axios.min.js&quot;&gt;&lt;/script&gt;      &lt;style&gt;          /* 简单的样式 */          #app &#123;              margin: 50px;              text-align: center;          &#125;          input, button &#123;              margin: 10px;          &#125;      &lt;/style&gt;  &lt;/head&gt;  &lt;body&gt;    &lt;div id=&quot;app&quot;&gt;      &lt;input type=&quot;text&quot; v-model=&quot;city&quot; placeholder=&quot;Enter city name&quot; @keyup.enter=&quot;getWeather&quot;&gt;      &lt;button @click=&quot;getWeather&quot;&gt;Get Weather&lt;/button&gt;      &lt;br/&gt;    &#123;&#123;weather.location[0].fxLink&#125;&#125;    &lt;p v-html=&quot;weather.location[0].fxLink&quot;&gt;&lt;/div&gt;    &lt;script&gt;      // Vue实例      new Vue(&#123;          el: &#x27;#app&#x27;,          data: &#123;              city: &#x27;&#x27;,              weather: &#123;                    code:200,                    location:[                        &#123;                           name: &quot;海淀&quot;,                            id: &quot;101010200&quot;,                            lat: &quot;39.95607&quot;,                            lon: &quot;116.31032&quot;,                            adm2: &quot;北京&quot;,                            adm1: &quot;北京市&quot;,                            country: &quot;中国&quot;,                            tz: &quot;Asia/Shanghai&quot;,                            utcOffset: &quot;+08:00&quot;,                            isDst: &quot;0&quot;,                            type: &quot;city&quot;,                            rank: &quot;15&quot;,                            fxLink: &quot;https://www.qweather.com/weather/haidian-101010200.html&quot;                        &#125;                    ],                            &#125;          &#125;,          methods: &#123;              getWeather: function() &#123;                  if (!this.city) &#123;                      alert(&#x27;Please enter a city name!&#x27;);                      return;                  &#125;                  var vm = this;                  axios.get(                    &quot;https://geoapi.qweather.com/v2/city/lookup?location=&quot; + this.city + &quot;&amp;key=50a831558a6e47efaf68994e101b0e42&quot;)                      .then(function(response) &#123;                          vm.weather = response.data;                      &#125;)                      .catch(function(error) &#123;                          console.error(&#x27;Error fetching weather:&#x27;, error);                          alert(&#x27;Failed to fetch weather.&#x27;);                      &#125;);              &#125;          &#125;      &#125;);  &lt;/script&gt;    &lt;/body&gt;  &lt;/html&gt;

