在test&#x2F;java中编写测试类EmployeeTestimport com.zdx.pojo.Employee;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.junit.After;import org.junit.Before;import org.junit.Test;import java.io.IOException;import java.io.Reader;public class EmployeeTest &#123;    private SqlSessionFactory sqlSessionFactory;    private SqlSession sqlSession;    @Before    public void init()&#123;        String resources =&quot;mybatis-config.xml&quot;;        Reader reader=null;        try &#123;            reader= Resources.getResourceAsReader(resources);            SqlSessionFactory sqlMapper=new SqlSessionFactoryBuilder().build(reader);            sqlSession=sqlMapper.openSession();        &#125; catch (IOException e) &#123;            e.printStackTrace();        &#125;    &#125;    @Test    public void testSelectAll()&#123;        Employee employee=sqlSession.selectOne(&quot;com.zdx.mapper.EmployeeMapper.findAll&quot;);        System.out.println(employee);    &#125;    @Test    public void testDelete()&#123;        Employee employee=new Employee(1,&quot;张三&quot;,20,&quot;manager&quot;);        sqlSession.delete(&quot;com.zdx.mapper.EmployeeMapper.delete&quot;,employee);    &#125;    @Test    public void testInsert()&#123;        Employee employee=new Employee(1,&quot;张三&quot;,20,&quot;manager&quot;);        sqlSession.insert(&quot;com.zdx.mapper.EmployeeMapper.insert&quot;,employee);    &#125;    @Test    public void testUpdate()&#123;        Employee employee=new Employee(1,&quot;张三&quot;,20,&quot;manager&quot;);        sqlSession.update(&quot;com.zdx.mapper.EmployeeMapper.update&quot;,employee);        System.out.println(employee.getName().toString());    &#125;    @After    public void destroy()&#123;        sqlSession.commit();        sqlSession.close();    &#125;&#125;

